import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, ImageBackground} from 'react-native';

class About extends React.Component {
    constructor() {
        super()
        this.state = {val:0}
        this.update = this.update.bind(this)
    }
    update() {
        this.setState({val: this.state.val + 1})

    }
    componentDidMount() {
        console.log("the component now successfully exists")

    }
    render() {
        return (  <ImageBackground source={{uri: 'https://www.cuded.com/wp-content/uploads/2014/04/41-Purple-Background.jpg'}} style={styles.backgroundImage}>
        <View style = { styles.container } > 
        <Text style={styles.title}> About Page</Text>
        <Text style={styles.text}> Movie Hub is a new website where users can find all streaming links to a movie. If you are looking to watch a movie of your choice, sign up to our website!</Text>
        <StatusBar style = "auto" />
        </View>
        </ImageBackground>

        )
    }
}
